mapper.insert:
INSERT INTO company (uid, level, logo, name, describe, address, lat, lng, create_date)
VALUES
(#{body.uid},#{body.level},#{body.logo},#{body.name},#{body.describe},#{body.address},#{body.lat},#{body.lng},NOW());

mapper.update:
UPDATE company
value=#{body.value}, level=#{body.level}, logo=#{body.logo}, name=#{body.name}, describe=#{body.describe}, address=#{body.address}, lat=#{body.lat}, lng=#{body.lng}, uid=#{body.uid},
WHERE id=#{body.id};

mapper.select:
SELECT * FROM company


        name like concat('%',#{key},'%')


        and level = #{level}


limit #{position},#{size};
